package loops;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int row=5, col=4;
		   for(int i=1;i<=row;i++) {
			   
			   for(int j=1;j<=col;j++) {
				   
				   System.out.print("* ");
				   
				   
			   }
			   System.out.println("");
			   
			   
		   }

		
		
		
		
		
	}

}
