package loops;

public class TestForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("print 1 to 5 number");
     for(int i=1;i<=5;i++);
     { 
    	     System.out.println('i' + " ");
    	 
     }
		System.out.println("");
		System.out.println("print 1 to 5 as a reverse");
		for(int i=5;i>=1;i--)
		{
			System.out.println(i + " ");
			
		}
		
	}

}
